select.execute:
function get_char_url(r, n) {
    return "http://www.wowarmory.com/character-sheet.xml?" +
            "n=" + (n) + "&r=" + (r);
}

var agent = "http://paulisageek.com/wowduel/ Firefox/3.0";

function char_info(r, n) {
    var response = y.rest(get_char_url(r, n))
     .header("User-Agent", agent)
     .get().response;

    var ilevel = 0;
    var items = response.characterInfo.characterTab.items.item;
    var ret = []
    var item_icons = [];
    for each (var item in items) {
        var id = item.@id;
        item_icons.push({"icon" : item.@icon.toString(), "id" : id.toString()});
        var url = "http://www.wowarmory.com/item-info.xml?" +
            "i=" + encodeURIComponent(id);

        var item_info = y.rest(url).header("User-Agent", agent).get().response;

        ilevel += parseInt(item_info.itemInfo.item.@level);
    }
    return {"items" : item_icons, "ilevel" : ilevel};
}

response.object = {
    "left": char_info(left_server, left_name),
    "right" : char_info(right_server, right_name),
}
